import React, { memo, useMemo } from "react";

import { Task } from "../../types/index";

interface ArrayItems {
  arrItems: Task[];
}

const NumberItems = ({ arrItems }: ArrayItems) => {
  const number = useMemo(
    () => arrItems.filter((item) => item.completed === false).length,
    [arrItems]
  );
  return (
    <p className="numberItems">
      <b>{number} </b> items left
    </p>
  );
};

export default memo(NumberItems);
