import React, { useState } from "react";
import TodoForm from "../../components/TodoForm";
import { TodoList } from "../../components/TodoList";
import "./style.scss";
import { Task } from "../../types/index";

const MainPage = () => {
  const [todo, setTodo] = useState<Task[]>([]);

  const AddHabdler = (title: string) => {
    const task: Task = {
      title,
      id: new Date().getTime(),
      completed: false,
    };

    setTodo((prev: Task[]) => {
      return [...prev, task];
    });
  };
  console.log(todo);

  return (
    <div className="container">
      <h1>Todo List</h1>
      <TodoForm onSubmit={AddHabdler} />
      <TodoList todo={todo} />
    </div>
  );
};

export default MainPage;
