import React from "react";
import { FILTER } from "../../constants/filter";
import NumberItems from "../NumberItems";
import ButtonClear from "../ButtonClear";
import TodoFilter from "../TodoFilter";
import { Task } from "../../types/index";

interface ArrayItems {
  arrItems: Task[];
  clear: () => void;
  setFilter: (title: string) => void;
  state: string;
}
const TodoInteraction = ({ arrItems, clear, state, setFilter }: ArrayItems) => {
  return (
    <div className="interaction">
      <NumberItems arrItems={arrItems} />
      <div className="filter__condition">
        {Object.values(FILTER).map((value) => (
          <TodoFilter
            key={value}
            item={value}
            state={state}
            setFilter={setFilter}
          />
        ))}
      </div>

      <ButtonClear clear={clear} />
    </div>
  );
};

export default TodoInteraction;
