import React, { useCallback } from "react";
import { connect } from "react-redux";
import { addTodo } from "../../actions";

import { ADD_NEWONE_TASK } from "../../constants";

interface Props {
  dispatch: any;
  placeHolder: string;
  action?: (...a: any) => { type: string; payload?: any };
  actionArguments?: {
    [key: string]: any;
  };
}

const InputMain = ({
  dispatch,
  placeHolder,
  action,
  actionArguments,
}: Props) => {
  let input: any;

  const onClickItemCheckbox = useCallback((e) => {
    e.preventDefault();
    if (!input.value.trim()) {
      return;
    }
    // dispatch(action(actionArguments ? actionArguments : input.value))
    actionArguments
      ? dispatch(action(input.value, actionArguments))
      : dispatch(addTodo(input.value));
    // dispatch(addTodo(input.value));
    input.value = "";
  }, []);

  return (
    <form
      onSubmit={
        onClickItemCheckbox
        // (e: any) => {
        // e.preventDefault();
        // if (!input.value.trim()) {
        //   return;
        // }
        //   dispatch(action(actionArguments ? actionArguments : input.value))
        // input.value = "";
        // }
      }
    >
      <input placeholder={placeHolder} ref={(node) => (input = node)}></input>
    </form>
  );
};

export default connect()(InputMain);
