import React, { useCallback } from "react";

interface ArrayFilter {
  item: string;
  clear?: () => void;
  state: string;
  setFilter: (title: string) => void;
}

export const TodoFilter = ({ item, state, setFilter }: ArrayFilter) => {
  const handleSelect = useCallback(() => {
    setFilter(item);
  }, [item]);

  return (
    <div
      className={`filter__button ${item === state ? state : ""}`}
      onClick={handleSelect}
    >
      <p>{item}</p>
    </div>
  );
};
