import React, { useEffect, useState } from "react";
import TodoForm from "../../components/TodoForm";
import { TodoList } from "../../components/TodoList";
import { TodoInteraction } from "../../components/TodoInteraction";
import "./style.scss";
import { Task } from "../../types/index";

const MainPage = () => {
  const [todo, setTodo] = useState<Task[]>([]);
  const [currentState, setCuttrentState] = useState<string>("all");

  const AddHabdler = (title: string) => {
    const task: Task = {
      title,
      id: new Date().getTime(),
      completed: false,
    };

    setTodo((prev: Task[]) => {
      return [...prev, task];
    });
  };

  const CheckHandler = (id: number) => {
    setTodo((prev) =>
      prev.map((todoItem) => {
        if (todoItem.id === id) {
          todoItem.completed = !todoItem.completed;
        }
        return todoItem;
      })
    );
  };

  const DeleteHandler = (id: number) => {
    setTodo((prev) => prev.filter((todoItem) => todoItem.id !== id));
  };

  const EditHandler = (id: number, title: string) => {
    setTodo((prev) =>
      prev.map((todoItem) => {
        if (todoItem.id === id) {
          todoItem.title = title;
        }
        return todoItem;
      })
    );
    if (title.trim() === "") {
      DeleteHandler(id);
    }
  };

  const CleanCompletedHandler = () => {
    setTodo((prev) => prev.filter((todoItem) => todoItem.completed === false));
  };

  const FilterStateHandler = (title: string) => {
    setCuttrentState(title);
  };

  const storeFiltered = (todoArr: Task[], currentStateStr: string) => {
    let arrStore;
    currentStateStr === "all"
      ? (arrStore = todoArr)
      : (arrStore = todoArr.filter((item) => {
          return item.completed === (currentStateStr === "completed");
        }));

    return arrStore;
  };

  const SelectAllHandler = () => {};

  useEffect(() => {
    setTodo(todo);
  }, [todo]);

  return (
    <div className="container">
      <h1>Todo List</h1>
      <TodoForm onSubmit={AddHabdler} />
      <TodoList
        todo={storeFiltered(todo, currentState)}
        onCheck={CheckHandler}
        onDelete={DeleteHandler}
        onEdidt={EditHandler}
      />
      {todo.length !== 0 ? (
        <TodoInteraction
          arrItems={todo}
          clear={CleanCompletedHandler}
          state={currentState}
          setFilter={FilterStateHandler}
        />
      ) : null}
    </div>
  );
};

export default MainPage;
