import React from "react";
import { connect } from "react-redux";
import { addTodo } from "../../actions";

import { ADD_NEWONE_TASK } from "../../constants";

interface Props {
  dispatch: any;
  placeHolder: string;
}

const InputMain = ({ dispatch, placeHolder }: Props) => {
  let input: any;
  return (
    <form
      onSubmit={(e: any) => {
        e.preventDefault();
        if (!input.value.trim()) {
          return;
        }

        if (addTodo(input.value).type === ADD_NEWONE_TASK) {
          dispatch(addTodo(input.value));
        } else {
          dispatch(addTodo(input.value));
        }

        input.value = "";
      }}
    >
      <input placeholder={placeHolder} ref={(node) => (input = node)}></input>
    </form>
  );
};

export default connect()(InputMain);
