import React, { useState } from "react";
import TodoForm from "../../components/TodoForm";
import { TodoList } from "../../components/TodoList";
import "./style.scss";
import { Task } from "../../types/index";

const MainPage = () => {
  const [load, serLoad] = useState<Task[]>([]);
  const [todo, setTodo] = useState<Task[]>([]);

  const AddHabdler = (title: string) => {
    const task: Task = {
      title,
      id: new Date().getTime(),
      completed: false,
    };

    setTodo((prev: Task[]) => {
      return [...prev, task];
    });
  };

  const CheckHandler = (id: number) => {
    // setTodo((prev) =>
    //   prev.map((todoItem) => {
    //     if (todoItem.id === id) {
    //       todoItem.completed = !todoItem.completed;
    //     }
    //     return todoItem;
    //   })
    // );
    const storeArr = todo;
    storeArr.forEach((item) => {
      if (item.id === id) {
        item.completed = !item.completed;
      }
    });
    setTodo(storeArr);
    serLoad(todo);
    console.log(todo);
  };

  const DeleteHandler = (id: number) => {};

  const EditHandler = (id: number, title: string) => {
    console.log(id, title);
  };

  return (
    <div className="container">
      <h1>Todo List</h1>
      <TodoForm onSubmit={AddHabdler} />
      <TodoList
        todo={todo}
        onCheck={CheckHandler}
        onDelete={DeleteHandler}
        onEdidt={EditHandler}
      />
    </div>
  );
};

export default MainPage;
