import React, { useCallback } from "react";
import { Button } from "../ButtonModal";

interface Modal {
  title: () => string;
  onClose: () => void;
  onConfirm: () => void;
}
export const TodoModal = ({ title, onClose, onConfirm }: Modal) => {
  const handleConfirm = useCallback(() => {
    onConfirm();
    onClose();
  }, [onConfirm, onClose]);

  return (
    <div className="modal__wrapper">
      <div className="modal__background" onClick={onClose}></div>
      <div className="modal">
        <div className="title">
          <span>"Do you want to delete?"</span>
        </div>
        <div className="description">
          <span>{title}</span>
        </div>
        <div>
          <div className="modal__button">
            <Button onClick={handleConfirm} title={"Yes"} />
            <Button onClick={onClose} title={"No"} />
          </div>
        </div>
      </div>
    </div>
  );
};
