import React, { useCallback } from "react";
import Button from "../ButtonModal";

interface Task {
  title: string;
  id: number;
  completed: boolean;
}
interface Modal {
  modalObj: Task[];
  onClose: () => void;
  onConfirm: () => void;
}
const TodoModal = ({ modalObj, onClose, onConfirm }: Modal) => {
  const handleConfirm = useCallback(() => {
    onConfirm();
    onClose();
  }, [onConfirm, onClose]);

  return (
    <div className="modal__wrapper">
      <div className="modal__background" onClick={onClose}></div>
      <div className="modal">
        <div className="title">
          <span>"Do you want to delete?"</span>
        </div>
        <div className="description">
          <span>{modalObj[0].title}</span>
        </div>
        <div>
          <div className="modal__button">
            <Button onClick={handleConfirm} titles={"Yes"} />
            <Button onClick={onClose} titles={"No"} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default TodoModal;
