import React from "react";
import { connect } from "react-redux";
import { addTodo } from "../../actions";

interface Props {
  dispatch: any;
}

const InputMain = ({ dispatch }: Props) => {
  let input: any;
  return (
    <form
      onSubmit={(e: any) => {
        e.preventDefault();
        dispatch(addTodo(input.value));
        input.value = "";
      }}
    >
      <input placeholder="hey here" ref={(node) => (input = node)}></input>
    </form>
  );
};

export default connect()(InputMain);
