import { ADD_NEWONE_TASK } from "../constants";
const todo = (state = [], actions: any) => {
  switch (actions.type) {
    case ADD_NEWONE_TASK:
      return [
        ...state,
        {
          id: actions.id,
          payload: actions.payload,
          completed: actions.completed,
        },
      ];
    // case DELETE_:
    //   return [
    //     ...state,
    //     {
    //       id: actions.id,
    //       payload: actions.payload,
    //       completed: actions.completed,
    //     },
    //   ];
    default:
      return state;
  }
};

export default todo;
