import React, { useState } from "react";
import { Task } from "../../types/index";
import { InputTask } from "../InputTask";
import checkImg from "../../asset/images/check.svg";
import uncheckImg from "../../asset/images/uncheck.svg";
import crossImg from "../../asset/images/cross.svg";

interface itemOne {
  item: Task;
}

export const TodoTask = ({ item }: itemOne) => {
  const [edit, setEdit] = useState<boolean>(true);

  const onEditClose = () => {
    // store.setItemData(submitValue, itemObj.id)
    setEdit(true);
    // onSubmitItemsInput()
  };

  const onEdit = () => {
    setEdit(false);
  };

  return (
    <div className={`todotask__wrapper ${edit ? "bbm_1" : "bbm_1_g"}`}>
      <div className="ckeckbox">
        <img
          src={item.completed ? uncheckImg : checkImg}
          alt="checkbox"
          className={`ckeckbox__img ${edit ? null : "opacity"}`}
        />
      </div>
      <div className="title">
        {edit ? (
          <p onDoubleClick={onEdit}>{item.title} </p>
        ) : (
          <InputTask onSubmit={onEditClose} />
        )}
      </div>
      <div className="cross">
        <img
          src={crossImg}
          alt="cross"
          className={`cross__Img  ${edit ? null : "opacity"}`}
        />
      </div>
    </div>
  );
};
