import React, { useState } from "react";
import selectImg from "../../asset/images/select.svg";
import unselectImg from "../../asset/images/unselect.svg";
import { Task } from "../../types/index";
interface Select {
  onSelectBtn: () => void;
  todo: Task[];
}

export const ButtonSelectAll = ({ onSelectBtn, todo }: Select) => {
  const [all, setAll] = useState<boolean>(false);
  const onSelect = () => {
    setAll(!all);
    onSelectBtn();
  };
  return (
    // use to swap directory then css hide
    <>
      <img
        src={all ? selectImg : unselectImg}
        alt="select"
        className={`select todo? '': opacity`}
        onClick={onSelect}
      />
    </>
  );
};
