import React, { useCallback } from "react";
import { Task } from "../../types";
import { Button } from "../ButtonModal";

interface Modal {
  modalId: number;
  description: Task[];
  onClose: () => void;
  onConfirm: () => void;
}
export const TodoModal = ({
  modalId,
  description,
  onClose,
  onConfirm,
}: Modal) => {
  const handleConfirm = useCallback(() => {
    onConfirm();
    onClose();
  }, [onConfirm, onClose]);

  const DescriptionTitle = (descriptionT: Task[], modalIdT: number) => {
    descriptionT.filter((desctItem) => {
      desctItem.id === modalIdT;
      //   return desctItem.title;
    });
  };
  console.log(DescriptionTitle(description, modalId));

  return (
    <div className="modal__wrapper">
      <div className="modal__background" onClick={onClose}></div>
      <div className="modal">
        <div className="title">
          <span>"Do you want to delete?"</span>
        </div>
        <div className="description">
          <span>asd</span>
        </div>
        <div>
          <div className="modal__button">
            <Button onClick={handleConfirm} title={"Yes"} />
            <Button onClick={onClose} title={"No"} />
          </div>
        </div>
      </div>
    </div>
  );
};
