import React, { useState } from "react";
import { Task } from "../../types/index";
import { InputTask } from "../InputTask";
import checkImg from "../../asset/images/check.svg";
import uncheckImg from "../../asset/images/uncheck.svg";
import crossImg from "../../asset/images/cross.svg";

interface itemOne {
  item: Task;
}

export const TodoTask = ({ item }: itemOne) => {
  const [edit, setEdit] = useState<boolean>(true);

  return (
    <div className="todotask__wrapper">
      <div className="ckeckbox">
        <img
          src={item.completed ? uncheckImg : checkImg}
          alt="checkbox"
          className="ckeckbox__img"
        />
      </div>
      <div className="title">
        {edit ? (
          <div>
            <p className="fz-20">{item.title}</p>
          </div>
        ) : (
          <InputTask />
        )}
      </div>
      <div className="cross">
        <img src={crossImg} alt="cross" className="cross__Img" />
      </div>
    </div>
  );
};
